tool.command:
	ln -f -s $bwOutput_file $bwOutput_file\.bw;
	docker run -v $__root_dir__/database/files:$__root_dir__/database/files -v $__root_dir__/data:$__root_dir__/data -u `id -u`:`id -g`  mmbirb/nucldyn:galaxy0.3  periodicity --calls $gff_file --reads $rdata_file 
 	#if $assembly.source == "buildin":
	--chrom_sizes ${assembly.ref_chrom_sizes_buildin} --genes ${assembly.ref_genome_gff_buildin} 
	#else if $assembly.source == "history":
	--chrom_sizes ${assembly.ref_chrom_sizes} --genes ${assembly.ref_genome_gff_history}
	#end if
	--gffOutput $output_gff_file --type $seq_type --bwOutput $bwOutput_file\.bw --periodicity $periodicity 2>&1;
	rm  $bwOutput_file\.bw
