tool.command:
  ln -f -s $bwOutput_file $bwOutput_file\.bw;
  docker run -v $__root_dir__/database/files:/data_dir -u `id -u`:`id -g`  mmbirb/nucldyn:galaxy galaxy periodicity --calls $gff_file --reads $rdata_file --gffOutput $output_gff_file --type $seq_type --bwOutput /data_dir/tmp.bw --genes $ref_genes_gff_file --chrom_sizes $ref_chrom_sizes --periodicity $periodicity 2>&1;
mv  tmp.bw $bwOutput_file
